        if let user = sender as? User {
            let destination2 = segue.destination as! DetaillViewController
            let destinationVC = segue.destination as! DetaillViewController
            destination2.newText2 = user.Howuneeddrinkpreporats
            destinationVC.newText = user.nameofMedicament
            
        }

    }

    

